import BaseHero from '@/layout/BaseHero.vue';
import SectionWhite from '@/layout/SectionWhite.vue';
import SectionOrange from '@/layout/SectionOrange.vue';
import BigMap from '@/components/BigMap.vue';
import BaseTextBlock from '@/components/BaseTextBlock.vue';
import MainSection from '@/layout/MainSection';
import BaseSidebar from '@/components/BaseSidebar';
import BaseTooltip from '@/components/BaseTooltip.vue';
import BaseNumberBlock from "@/components/BaseNumberBlock.vue";
import BaseCollageInfo from '@/components/BaseCollageInfo.vue';
import BaseDropdownInfo from '@/components/BaseDropdownInfo.vue';
import gsap from "gsap";
import ScrollTrigger from "gsap/ScrollTrigger";
gsap.registerPlugin(ScrollTrigger);
export default {
  components: {
    BaseHero,
    SectionOrange,
    SectionWhite,
    BigMap,
    BaseTextBlock,
    MainSection,
    BaseSidebar,
    BaseTooltip,
    BaseNumberBlock,
    BaseCollageInfo,
    BaseDropdownInfo
  },
  data() {
    return {
      map1: 1,
      map2: 2,
      idMap: 1,
      circales: [{
        color: 'green',
        number: '1'
      }, {
        color: 'blue',
        number: '2'
      }, {
        color: 'blue',
        number: '3'
      }, {
        color: 'blue',
        number: '4'
      }, {
        color: 'purple',
        number: '5'
      }, {
        color: 'purple',
        number: '6'
      }, {
        color: 'purple',
        number: '7'
      }],
      isMobile: window.innerWidth <= 768,
      heroDescr: 'Какие виды животных и&nbsp;растений живут по&nbsp;соседству с&nbsp;промышленным гигантом&nbsp;Красноярского края&nbsp;&mdash; Большим Норильском'
    };
  },
  mounted() {
    this.$nextTick(function () {
      this.scrollAnimation();
      this.parallax();
    });
  },
  methods: {
    onResize() {
      this.isMobile = window.innerWidth <= 768;
    },
    scrollMobile() {
      const collageItems = Array.from(document.querySelectorAll(".scroll-bg.active"));
      collageItems.forEach(elem => {
        elem.css('background-position', '0px ' + document.scrollTop() + 'px');
      });
    },
    scrollAnimation() {
      const collageItems = Array.from(document.querySelectorAll(".text-block__wrap"));
      collageItems.forEach(elem => {
        this.oneScrollTrigger = gsap.fromTo(elem, {
          y: 50
        }, {
          y: 0,
          duration: 1,
          scrollTrigger: {
            start: '0% 100%',
            end: 'bottom 75%',
            trigger: elem,
            scrub: true
            // markers: true,
          }
        });
      });
      const sidebarItems = Array.from(document.querySelectorAll(".sidebar"));
      sidebarItems.forEach(elem => {
        this.twoScrollTrigger = gsap.fromTo(elem, {
          y: 50
        }, {
          y: 0,
          duration: 1,
          scrollTrigger: {
            start: '0% 100%',
            end: 'bottom 75%',
            trigger: elem,
            // markers: true,
            scrub: true
          }
        });
      });
      this.ScrollTrigger = gsap.to(".scroll-bg", {
        scrollTrigger: {
          trigger: ".scroll",
          start: '0%',
          scrub: true,
          toggleClass: {
            className: "active",
            targets: ".scroll-bg"
          }
        }
      });
      if (window.innerWidth <= 768) {
        this.scrollMobile();
      }
      gsap.fromTo(".bg-1", {
        opacity: '1'
      }, {
        opacity: '0',
        scrollTrigger: {
          trigger: ".card-1",
          start: 'top 0%',
          end: 'bottom 50%',
          endTrigger: '.card-2',
          // markers: true,
          scrub: true
        }
      });
      gsap.fromTo(".bg-2", {
        opacity: '1'
      }, {
        opacity: '0',
        scrollTrigger: {
          trigger: ".card-2",
          start: 'top 0%',
          end: 'bottom 50%',
          endTrigger: '.card-3',
          // markers: true,
          scrub: true
        }
      });
      gsap.to(".scroll-bg", {
        scrollTrigger: {
          trigger: ".card-3",
          start: () => "+=120%",
          end: () => "+=" + document.querySelector(".scroll-wrap").offsetHeight,
          // markers: true,
          scrub: true,
          toggleClass: {
            className: "remove-active",
            targets: ".scroll-bg"
          }
        }
      });
    },
    parallax() {
      if (window.innerWidth > 768) {
        gsap.fromTo(".collage__eagle", {
          y: 105
        }, {
          y: -20,
          scrollTrigger: {
            trigger: ".collage",
            start: 'top 100%',
            end: '13%',
            // markers: true,
            scrub: true
          }
        });
        gsap.fromTo(".collage__merlin", {
          y: 55
        }, {
          y: -15,
          scrollTrigger: {
            trigger: ".collage",
            start: '25% 100%',
            end: '33%',
            // markers: true,
            scrub: true
          }
        });
        gsap.fromTo(".collage__loach", {
          y: 50
        }, {
          y: -0,
          scrollTrigger: {
            trigger: ".collage",
            start: '90% 100%',
            end: 'bottom',
            // markers: true,
            scrub: true
          }
        });
        gsap.fromTo(".collage__loon", {
          y: 35
        }, {
          y: -10,
          scrollTrigger: {
            trigger: ".collage",
            start: '80% 100%',
            end: '100%',
            // markers: true,
            scrub: true
          }
        });
      }
    }
  }
};
