import BaseHero from '@/layout/BaseHero.vue';
import SectionOrange from '@/layout/SectionOrange.vue';
import BigMap from '@/components/BigMap.vue';
import SectionWhite from '@/layout/SectionWhite.vue';
import MainSection from '@/layout/MainSection';
import BaseTooltip from '@/components/BaseTooltip.vue';
import BaseTextBlock from '@/components/BaseTextBlock.vue';
import BaseSidebar from '@/components/BaseSidebar';
import BaseNumberBlock from '@/components/BaseNumberBlock';
import BaseDropdownInfo from '@/components/BaseDropdownInfo.vue';
import BaseCollageInfo from '@/components/BaseCollageInfo.vue';
import TransbaikalScrollSwiper from '@/components/TransbaikalScrollSwiper.vue';
import gsap from "gsap";
import ScrollTrigger from "gsap/ScrollTrigger";
gsap.registerPlugin(ScrollTrigger);
export default {
  components: {
    BaseHero,
    SectionOrange,
    SectionWhite,
    BigMap,
    BaseTextBlock,
    MainSection,
    BaseSidebar,
    BaseTooltip,
    TransbaikalScrollSwiper,
    BaseDropdownInfo,
    BaseNumberBlock,
    BaseCollageInfo
  },
  data() {
    return {
      isMobile: window.innerWidth <= 768,
      circales: [{
        color: 'green',
        number: '1'
      }, {
        color: 'purple',
        number: '2'
      }],
      heroDescrEn: 'Which species neighbour one of Nornickel’s most hi-tech and&nbsp;advanced&nbsp;divisions?',
      heroDescr: 'Кто обитает на&nbsp;территории одного из&nbsp;самых современных и&nbsp;технологичных дивизионов &laquo;Норникеля&raquo;'
    };
  },
  computed: {
    lang() {
      return this.$store.state.lang;
    }
  },
  mounted() {
    this.$nextTick(function () {
      this.scrollAnimation();
      this.parallax();
    });
  },
  methods: {
    scrollAnimation() {
      const collageItems = Array.from(document.querySelectorAll(".text-block__wrap"));
      collageItems.forEach(elem => {
        this.oneScrollTrigger = gsap.fromTo(elem, {
          y: 50
        }, {
          y: 0,
          duration: 1,
          scrollTrigger: {
            start: '0% 100%',
            end: 'bottom 75%',
            trigger: elem,
            scrub: true
            // markers: true,
          }
        });
      });
      const sidebarItems = Array.from(document.querySelectorAll(".sidebar"));
      sidebarItems.forEach(elem => {
        this.twoScrollTrigger = gsap.fromTo(elem, {
          y: 50
        }, {
          y: 0,
          duration: 1,
          scrollTrigger: {
            start: '0% 100%',
            end: 'bottom 75%',
            trigger: elem,
            // markers: true,
            scrub: true
          }
        });
      });
    },
    parallax() {
      if (window.innerWidth > 768) {
        gsap.fromTo(".collage__eagle", {
          y: 115
        }, {
          y: -20,
          scrollTrigger: {
            trigger: ".collage",
            start: 'top 100%',
            end: '12%',
            // markers: true,
            scrub: true
          }
        });
        gsap.fromTo(".collage__harrier", {
          y: 55
        }, {
          y: -15,
          scrollTrigger: {
            trigger: ".collage",
            start: '15% 100%',
            end: '22%',
            // markers: true,
            scrub: true
          }
        });
        gsap.fromTo(".collage__fish", {
          y: 35
        }, {
          y: -12,
          scrollTrigger: {
            trigger: ".collage",
            start: '93% 100%',
            end: 'bottom',
            // markers: true,
            scrub: true
          }
        });
        gsap.fromTo(".collage__curlew", {
          y: 50
        }, {
          y: -15,
          scrollTrigger: {
            trigger: ".collage",
            start: '78% 100%',
            end: '88%',
            // markers: true,
            scrub: true
          }
        });
      }
    }
  }
};
