import BaseHero from '@/layout/BaseHero.vue';
import MainSection from '@/layout/MainSection';
import SectionWhite from '@/layout/SectionWhite.vue';
import SectionOrange from '@/layout/SectionOrange.vue';
import BaseTextBlock from '@/components/BaseTextBlock.vue';
import BaseSidebar from '@/components/BaseSidebar';
import BaseNumberBlock from "@/components/BaseNumberBlock.vue";
import BasePhoto from "@/components/BasePhoto.vue";
import TreasuresScrollSwiper1 from "@/components/TreasuresScrollSwiper1.vue";
import TreasuresScrollSwiper2 from "@/components/TreasuresScrollSwiper2.vue";
import TreasuresScrollSwiper3 from "@/components/TreasuresScrollSwiper3.vue";
import TreasuresScrollSwiper4 from "@/components/TreasuresScrollSwiper4.vue";
import gsap from "gsap";
import ScrollTrigger from "gsap/ScrollTrigger";
gsap.registerPlugin(ScrollTrigger);
export default {
  components: {
    BaseHero,
    SectionOrange,
    SectionWhite,
    BaseTextBlock,
    MainSection,
    BaseSidebar,
    TreasuresScrollSwiper1,
    TreasuresScrollSwiper2,
    TreasuresScrollSwiper3,
    TreasuresScrollSwiper4,
    BasePhoto,
    BaseNumberBlock
  },
  data() {
    return {
      heroDescrEn: 'Nornickel has been cooperating with nature reserves and protected areas of Taimyr, the Trans-Bailkal Territory, and the Kola Peninsula for more than ten years. With support from the сompany, specially protected areas are developing new environmental routes and implementing programmes to explore and preserve rare species of animals and plants.',
      heroDescr: '&laquo;Норникель&raquo; на&nbsp;протяжении более чем&nbsp;десяти лет сотрудничает с&nbsp;заповедниками и&nbsp;заказниками Таймыра, Забайкалья и&nbsp;Кольского полуострова. При поддержке компании на&nbsp;особо охраняемых территориях появляются новые экологические маршруты и&nbsp;реализуются программы по&nbsp;изучению и&nbsp;сохранению редких видов животных и&nbsp;растений.'
    };
  },
  mounted() {
    this.$nextTick(function () {
      this.scrollAnimation();
    });
  },
  computed: {
    lang() {
      return this.$store.state.lang;
    }
  },
  methods: {
    scrollAnimation() {
      const collageItems = Array.from(document.querySelectorAll(".text-block__wrap"));
      collageItems.forEach(elem => {
        this.oneScrollTrigger = gsap.fromTo(elem, {
          y: 50
        }, {
          y: 0,
          duration: 1,
          scrollTrigger: {
            start: '0% 100%',
            end: 'bottom 75%',
            trigger: elem,
            scrub: true
            // markers: true,
          }
        });
      });
      const sidebarItems = Array.from(document.querySelectorAll(".sidebar"));
      sidebarItems.forEach(elem => {
        this.twoScrollTrigger = gsap.fromTo(elem, {
          y: 50
        }, {
          y: 0,
          duration: 1,
          scrollTrigger: {
            start: '0% 100%',
            end: 'bottom 75%',
            trigger: elem,
            // markers: true,
            scrub: true
          }
        });
      });
    }
  }
};
