import BaseHero from '@/layout/BaseHero.vue';
import MainSection from '@/layout/MainSection';
import SectionWhite from '@/layout/SectionWhite.vue';
import BaseTextBlock from '@/components/BaseTextBlock.vue';
import BaseSidebar from '@/components/BaseSidebar';
import BaseDropdownInfo from '@/components/BaseDropdownInfo';
import gsap from "gsap";
import ScrollTrigger from "gsap/ScrollTrigger";
gsap.registerPlugin(ScrollTrigger);
export default {
  components: {
    BaseHero,
    SectionWhite,
    BaseTextBlock,
    MainSection,
    BaseSidebar,
    BaseDropdownInfo
  },
  data() {
    return {
      heroDescrEn: 'In the past, polar bears reigned supreme over the entire Arctic, with no serious adversaries in the north. However, the combined effects of global climate change and human presence have pushed these magnificent predators to the brink of extinction. Today, scientists estimate that there are no more than 25,000 polar bears left worldwide, with approximately 5,000 to 6,000 of them found in Russia.',
      heroDescr: 'Когда‑то вся Арктика принадлежала белым медведям: на&nbsp;Севере не&nbsp;было зверей, которые могли&nbsp;бы на&nbsp;равных соперничать с&nbsp;ними. Но&nbsp;глобальное изменение климата и&nbsp;соседство с&nbsp;людьми привело полярных хищников на&nbsp;грань вымирания: сейчас, по&nbsp;оценкам учёных, в&nbsp;мире осталось не&nbsp;более 25&nbsp;тыс. особей, из&nbsp;них 5&ndash;6&nbsp;тыс. живут на&nbsp;территории нашей страны.'
    };
  },
  mounted() {
    this.$nextTick(function () {
      this.scrollAnimation();
    });
  },
  methods: {
    scrollMobile() {
      const collageItems = Array.from(document.querySelectorAll(".scroll-bg.active"));
      collageItems.forEach(elem => {
        elem.css('background-position', '0px ' + document.scrollTop() + 'px');
      });
      const collageItems2 = Array.from(document.querySelectorAll(".scroll-2-bg.active"));
      collageItems2.forEach(elem => {
        elem.css('background-position', '0px ' + document.scrollTop() + 'px');
      });
    },
    scrollAnimation() {
      const collageItems = Array.from(document.querySelectorAll(".text-block__wrap"));
      collageItems.forEach(elem => {
        this.oneScrollTrigger = gsap.fromTo(elem, {
          y: 50
        }, {
          y: 0,
          duration: 1,
          scrollTrigger: {
            start: '0% 100%',
            end: 'bottom 75%',
            trigger: elem,
            scrub: true
          }
        });
      });
      const sidebarItems = Array.from(document.querySelectorAll(".sidebar"));
      sidebarItems.forEach(elem => {
        this.twoScrollTrigger = gsap.fromTo(elem, {
          y: 50
        }, {
          y: 0,
          duration: 1,
          scrollTrigger: {
            start: '0% 100%',
            end: 'bottom 75%',
            trigger: elem,
            scrub: true
          }
        });
      });
      gsap.to(".scroll-bg", {
        scrollTrigger: {
          trigger: ".scroll",
          start: '0%',
          scrub: true,
          toggleClass: {
            className: "active",
            targets: ".scroll-bg"
          }
        }
      });
      gsap.to(".scroll-2-bg", {
        scrollTrigger: {
          trigger: ".scroll-2",
          start: '0%',
          scrub: true,
          toggleClass: {
            className: "active",
            targets: ".scroll-2-bg"
          }
        }
      });
      if (window.innerWidth <= 768) {
        this.scrollMobile();
      }
      gsap.fromTo(".bg-1", {
        opacity: '1'
      }, {
        opacity: '0',
        scrollTrigger: {
          trigger: ".card-1",
          start: 'top 0%',
          end: 'bottom 50%',
          endTrigger: '.card-2',
          // markers: true,
          scrub: true
        }
      });
      gsap.fromTo(".bg-2", {
        opacity: '1'
      }, {
        opacity: '0',
        scrollTrigger: {
          trigger: ".card-2",
          start: 'top 0%',
          end: 'bottom 50%',
          endTrigger: '.card-3',
          scrub: true
        }
      });
      gsap.fromTo(".bg-3", {
        opacity: '1'
      }, {
        opacity: '0',
        scrollTrigger: {
          trigger: ".card-3",
          start: 'top 0%',
          end: 'bottom 50%',
          endTrigger: '.card-4',
          // markers: true,
          scrub: true
        }
      });
      gsap.fromTo(".bg-5", {
        opacity: '1'
      }, {
        opacity: '0',
        scrollTrigger: {
          trigger: ".card-5",
          start: 'top 0%',
          end: 'bottom 50%',
          endTrigger: '.card-6',
          // markers: true,
          scrub: true
        }
      });
      gsap.fromTo(".bg-6", {
        opacity: '1'
      }, {
        opacity: '0',
        scrollTrigger: {
          trigger: ".card-6",
          start: 'top 0%',
          end: 'bottom 50%',
          endTrigger: '.card-7',
          // markers: true,
          scrub: true
        }
      });
      gsap.to(".scroll-bg", {
        scrollTrigger: {
          trigger: ".card-4",
          start: () => "+=120%",
          end: () => "+=" + document.querySelector(".scroll-wrap").offsetHeight,
          // markers: true,
          scrub: true,
          toggleClass: {
            className: "remove-active",
            targets: ".scroll-bg"
          }
        }
      });
      gsap.to(".scroll-2-bg", {
        scrollTrigger: {
          trigger: ".card-7",
          start: () => "+=120%",
          end: () => "+=" + document.querySelector(".scroll-2-wrap").offsetHeight,
          // markers: true,
          scrub: true,
          toggleClass: {
            className: "remove-active",
            targets: ".scroll-2-bg"
          }
        }
      });
    }
  }
};
