import BaseHero from '@/layout/BaseHero.vue';
import BigMap from '@/components/BigMap.vue';
import SectionOrange from '@/layout/SectionOrange.vue';
import SectionWhite from '@/layout/SectionWhite.vue';
import MainSection from '@/layout/MainSection';
import BaseTooltip from '@/components/BaseTooltip.vue';
import BaseTextBlock from '@/components/BaseTextBlock.vue';
import BaseSidebar from '@/components/BaseSidebar';
import BaseCollageInfo from '@/components/BaseCollageInfo.vue';
import BaseDropdownInfo from '@/components/BaseDropdownInfo.vue';
import BaseNumberBlock from '@/components/BaseNumberBlock';
import gsap from "gsap";
import ScrollTrigger from "gsap/ScrollTrigger";
gsap.registerPlugin(ScrollTrigger);
export default {
  components: {
    BaseHero,
    SectionOrange,
    SectionWhite,
    BigMap,
    BaseTextBlock,
    MainSection,
    BaseSidebar,
    BaseTooltip,
    BaseDropdownInfo,
    BaseCollageInfo,
    BaseNumberBlock
  },
  data() {
    return {
      map1: 1,
      map2: 2,
      idMap: 1,
      isMobile: window.innerWidth <= 768,
      circales: [{
        color: 'green',
        number: '1'
      }, {
        color: 'green',
        number: '2'
      }, {
        color: 'purple',
        number: '3'
      }, {
        color: 'purple',
        number: '4'
      }],
      heroDescr: 'Растительный и&nbsp;животный мир рядом с&nbsp;промышленными площадками &laquo;Норникеля&raquo; в&nbsp;Мурманской области'
    };
  },
  mounted() {
    this.$nextTick(function () {
      this.scrollAnimation();
      this.parallax();
    });
  },
  methods: {
    onResize() {
      this.isMobile = window.innerWidth <= 768;
    },
    scrollMobile() {
      const collageItems = Array.from(document.querySelectorAll(".scroll-bg.active"));
      collageItems.forEach(elem => {
        elem.css('background-position', '0px ' + document.scrollTop() + 'px');
      });
    },
    scrollAnimation() {
      const collageItems = Array.from(document.querySelectorAll(".text-block__wrap"));
      collageItems.forEach(elem => {
        this.oneScrollTrigger = gsap.fromTo(elem, {
          y: 50
        }, {
          y: 0,
          duration: 1,
          scrollTrigger: {
            start: '0% 100%',
            end: 'bottom 75%',
            trigger: elem,
            scrub: true
            // markers: true,
          }
        });
      });
      const sidebarItems = Array.from(document.querySelectorAll(".sidebar"));
      sidebarItems.forEach(elem => {
        this.twoScrollTrigger = gsap.fromTo(elem, {
          y: 50
        }, {
          y: 0,
          duration: 1,
          scrollTrigger: {
            start: '0% 100%',
            end: 'bottom 75%',
            trigger: elem,
            // markers: true,
            scrub: true
          }
        });
      });
      this.ScrollTrigger = gsap.to(".scroll-bg", {
        scrollTrigger: {
          trigger: ".scroll",
          start: '0%',
          scrub: true,
          toggleClass: {
            className: "active",
            targets: ".scroll-bg"
          }
        }
      });
      if (window.innerWidth <= 768) {
        this.scrollMobile();
      }
      gsap.fromTo(".bg-1", {
        opacity: '1'
      }, {
        opacity: '0',
        scrollTrigger: {
          trigger: ".card-1",
          start: 'top 0%',
          end: 'bottom 50%',
          endTrigger: '.card-2',
          // markers: true,
          scrub: true
        }
      });
      gsap.fromTo(".bg-2", {
        opacity: '1'
      }, {
        opacity: '0',
        scrollTrigger: {
          trigger: ".card-2",
          start: 'top 0%',
          end: 'bottom 50%',
          endTrigger: '.card-3',
          // markers: true,
          scrub: true
        }
      });
      gsap.fromTo(".bg-3", {
        opacity: '1'
      }, {
        opacity: '0',
        scrollTrigger: {
          trigger: ".card-3",
          start: 'top 0%',
          end: 'bottom 50%',
          endTrigger: '.card-4',
          // markers: true,
          scrub: true
        }
      });
      gsap.to(".scroll-bg", {
        scrollTrigger: {
          trigger: ".card-4",
          start: () => "+=120%",
          end: () => "+=" + document.querySelector(".scroll-wrap").offsetHeight,
          // markers: true,
          scrub: true,
          toggleClass: {
            className: "remove-active",
            targets: ".scroll-bg"
          }
        }
      });
    },
    parallax() {
      if (window.innerWidth > 768) {
        gsap.fromTo(".collage__golden-eagle", {
          y: 115
        }, {
          y: -15,
          scrollTrigger: {
            trigger: ".collage",
            start: 'top 100%',
            end: '10%',
            // markers: true,
            scrub: true
          }
        });
        gsap.fromTo(".collage__bluethroat", {
          y: 55
        }, {
          y: -15,
          scrollTrigger: {
            trigger: ".collage",
            start: '24% 100%',
            end: '29%',
            // markers: true,
            scrub: true
          }
        });
        gsap.fromTo(".collage__swan", {
          y: 30
        }, {
          y: -10,
          scrollTrigger: {
            trigger: ".collage",
            start: '75% 100%',
            end: 'bottom',
            // markers: true,
            scrub: true
          }
        });
        gsap.fromTo(".collage__grayling", {
          y: 40
        }, {
          y: -10,
          scrollTrigger: {
            trigger: ".collage",
            start: '90% 100%',
            end: '100%',
            // markers: true,
            scrub: true
          }
        });
      }
    }
  }
};
