    BaseHero,
    BaseTextQuotes,
    BaseTextBlock,
    MainSection,
    BaseSidebar
  },
  data() {
    return {
      oneScrollTrigger: null,
      twoScrollTrigger: null,
      threeScrollTrigger: null,
      heroDescr: 'Как оценить состояние экосистемы? Как&nbsp;отличить естественные процессы в&nbsp;сообществе живых организмов от&nbsp;изменений, которые происходят под&nbsp;влиянием антропогенных и&nbsp;техногенных факторов? &laquo;Норникель&raquo; уделяет большое внимание сохранению биоразнообразия, и&nbsp;для&nbsp;него это важные вопросы. Чтобы&nbsp;получить на&nbsp;них ответ и&nbsp;эффективно отслеживать изменения, компания поручила учёным разработать показатель для оценки состояния экосистем. И&nbsp;учёные разработали для компании Интегральный показатель состояния экосистем, сокращённо&nbsp;&mdash; ИПСЭ.'
    };
  },
  mounted() {
    this.$nextTick(function () {
      this.scrollAnimation();
    });
  },
  beforeDestroy() {
    this.oneScrollTrigger.kill();
    this.oneScrollTrigger = null;
    this.twoScrollTrigger.kill();
    this.twoScrollTrigger = null;
    this.threeScrollTrigger.kill();
    this.threeScrollTrigger = null;
  },
  methods: {
    scrollMobile() {
      const collageItems = Array.from(document.querySelectorAll(".scroll-bg.active"));
      collageItems.forEach(elem => {
        elem.css('background-position', '0px ' + document.scrollTop() + 'px');
      });
      const collageItems2 = Array.from(document.querySelectorAll(".scroll-2-bg.active"));
      collageItems2.forEach(elem => {
        elem.css('background-position', '0px ' + document.scrollTop() + 'px');
      });
    },
    scrollAnimation() {
      const collageItems = Array.from(document.querySelectorAll(".text-block__wrap"));
      collageItems.forEach(elem => {
        this.oneScrollTrigger = gsap.fromTo(elem, {
          y: 50
        }, {
          y: 0,
          duration: 1,
          scrollTrigger: {
            start: '0% 100%',
            end: 'bottom 75%',
            trigger: elem,
            scrub: true
            // markers: true,
          }
        });
      });
      const sidebarItems = Array.from(document.querySelectorAll(".sidebar"));
      sidebarItems.forEach(elem => {
        this.twoScrollTrigger = gsap.fromTo(elem, {
          y: 50
        }, {
          y: 0,
          duration: 1,
          scrollTrigger: {
            start: '0% 100%',
            end: 'bottom 75%',
            trigger: elem,
            // markers: true,
            scrub: true
          }
        });
      });
      const quotesItems = Array.from(document.querySelectorAll(".text-quotes"));
      quotesItems.forEach(elem => {
        gsap.fromTo(elem, {
          y: 50
        }, {
          y: 0,
          duration: 1,
          scrollTrigger: {
            start: '0% 100%',
            end: 'bottom 75%',
            trigger: elem,
            scrub: true
            // markers: true,
          }
        });
      });
      if (window.innerWidth <= 768) {
        this.scrollMobile();
      }
      this.oneScrollTrigger = gsap.to(".scroll-bg", {
        scrollTrigger: {
          trigger: ".scroll",
          start: '0%',
          scrub: true,
          toggleClass: {
            className: "active",
            targets: ".scroll-bg"
          }
        }
      });
      gsap.fromTo(".bg-1", {
        opacity: '1'
      }, {
        opacity: '0',
        scrollTrigger: {
          trigger: ".card-1",
          start: 'top 0%',
          end: 'bottom 50%',
          endTrigger: '.card-2',
          // markers: true,
          scrub: true
        }
      });
      gsap.fromTo(".bg-2", {
        opacity: '1'
      }, {
        opacity: '0',
        scrollTrigger: {
          trigger: ".card-2",
          start: 'top 0%',
          end: 'bottom 50%',
          endTrigger: '.card-3',
          // markers: true,
          scrub: true
        }
      });
      gsap.to(".scroll-bg", {
        scrollTrigger: {
          trigger: ".card-3",
          start: () => "+=100%",
          end: () => "+=" + document.querySelector(".scroll-wrap").offsetHeight,
          // markers: true,
          scrub: true,
          toggleClass: {
            className: "remove-active",
            targets: ".scroll-bg"
          }
        }
      });
      this.animationBlock();
      this.twoScrollTrigger = gsap.to(".scroll-2-bg", {
        scrollTrigger: {
          trigger: ".scroll-2",
          start: 'top 20%',
          scrub: true,
          // markers: true,
          toggleClass: {
            className: "active",
            targets: ".scroll-2-bg"
          }
        }
      });
      gsap.fromTo(".bg-4", {
        opacity: '1'
      }, {
        opacity: '0',
        scrollTrigger: {
          trigger: ".card-4",
          start: 'top 0%',
          end: 'bottom 50%',
          endTrigger: '.card-5',
          // markers: true,
          scrub: true
        }
      });
      gsap.fromTo(".bg-5", {
        opacity: '1'
      }, {
        opacity: '0',
        scrollTrigger: {
          trigger: ".card-5",
          start: 'top 0%',
          end: 'bottom 50%',
          endTrigger: '.card-6',
          // markers: true,
          scrub: true
        }
      });
      gsap.to(".scroll-2-bg", {
        scrollTrigger: {
          trigger: ".card-6",
          start: () => "+=180%",
          end: () => "+=" + document.querySelector(".scroll-2-wrap").offsetHeight,
          // markers: true,
          scrub: true,
          toggleClass: {
            className: "remove-active",
            targets: ".scroll-2-bg"
          }
        }
      });
    },
    animationBlock() {
      if (window.innerWidth > 768) {
        this.threeScrollTrigger = gsap.timeline({
          scrollTrigger: {
            trigger: ".indicator__calculation",
            start: 'top 10%',
            end: '600%',
            // markers: true,
            pin: true,
            scrub: true
          }
        });
        this.threeScrollTrigger.to(".indicator__calculation-circle", {
          xPercent: 170
        }).to(".indicator__calculation-circle", {
          xPercent: 200,
          opacity: 0
        }).to(".indicator__calculation-circle-1", {
          xPercent: 370,
          yPercent: 70
        }).to(".indicator__calculation-circle-2", {
          xPercent: 492,
          yPercent: 0
        }, "<").to(".indicator__calculation-circle-3", {
          xPercent: 483,
          yPercent: -80
        }, "<").to(".indicator__calculation-circle-number", {
          display: "none"
        }, "<").to(".indicator__calculation-step-2", {
          opacity: 1
        }, "<").to(".indicator__calculation-circle", {
          opacity: 1
        }).to(".indicator__calculation-circle-1", {
          opacity: 1,
          repeat: 0,
          rotation: 360,
          transformOrigin: "50px 10%",
          ease: "linear"
        }).to(".indicator__calculation-circle-2", {
          opacity: 1,
          repeat: 0,
          rotation: 360,
          transformOrigin: "10px 70%",
          ease: "linear"
        }, "<").to(".indicator__calculation-circle-3", {
          opacity: 1,
          repeat: 0,
          rotation: 360,
          transformOrigin: "30px 50%",
          ease: "linear"
        }, "<").to(".indicator__calculation-circle-num", {
          opacity: 1
        }, "<").to(".indicator__calculation-circle", {
          opacity: 0
        }).to(".indicator__calculation-circle-num", {
          opacity: 1
        }, "<").to(".indicator__calculation-step-3", {
          opacity: 1
        }).to(".indicator__calculation-circle-4", {
          opacity: 1
        }, "<").to(".indicator__calculation-circle-4", {
          xPercent: 150
        });
      } else {
        this.threeScrollTrigger = gsap.timeline({
          scrollTrigger: {
            trigger: ".indicator__calculation",
            start: 'top 10%',
            end: '600%',
            // markers: true,
            pin: true,
            scrub: true
          }
        });
        this.threeScrollTrigger.to(".indicator__calculation-circle", {
          yPercent: 170
        }).to(".indicator__calculation-circle", {
          yPercent: 200,
          opacity: 0
        }).to(".indicator__calculation-circle-1", {
          yPercent: 350,
          xPercent: -95
        }).to(".indicator__calculation-circle-2", {
          yPercent: 492,
          xPercent: 0
        }, "<").to(".indicator__calculation-circle-3", {
          yPercent: 450,
          xPercent: 130
        }, "<").to(".indicator__calculation-circle-number", {
          display: "none"
        }, "<").to(".indicator__calculation-step-1", {
          display: "none",
          opacity: 0
        }, "<").to(".indicator__calculation-step-2", {
          display: "block",
          opacity: 1
        }).to(".indicator__calculation-circle", {
          opacity: 1
        }, "<").to(".indicator__calculation-circle-1", {
          opacity: 1,
          repeat: 0,
          rotation: -360,
          transformOrigin: "30px 10%",
          ease: "linear"
        }).to(".indicator__calculation-circle-2", {
          opacity: 1,
          repeat: 0,
          rotation: -360,
          transformOrigin: "10px 70%",
          ease: "linear"
        }, "<").to(".indicator__calculation-circle-3", {
          opacity: 1,
          repeat: 0,
          rotation: -360,
          transformOrigin: "20px 40%",
          ease: "linear"
        }, "<").to(".indicator__calculation-circle-num", {
          opacity: 1
        }, "<").to(".indicator__calculation-circle", {
          opacity: 0
        }).to(".indicator__calculation-circle-num", {
          opacity: 1
        }, "<").to(".indicator__calculation-step-2", {
          display: "none",
          opacity: 0
        }, "<").to(".indicator__calculation-step-3", {
          display: "block",
          opacity: 1
        }).to(".indicator__calculation-circle-4", {
          opacity: 1
        }, "<").to(".indicator__calculation-circle-4", {
          yPercent: 50
        });
      }
    }
  }
};
